{"settings":"{
	/////////////////////////////////////////////////////////
	// Main settings ////////////////////////////////////////
	/////////////////////////////////////////////////////////
	"window.confirmBeforeClose": "always",
	"window.commandCenter": false,
	"window.density.editorTabHeight": "default",
	"window.title": " ",
	// "window.titleBarStyle": "custom", // hide title bar on OS Linux
	"editor.largeFileOptimizations": false,
	"editor.folding": true,
	"editor.defaultFormatter": "esbenp.prettier-vscode",
	"editor.gotoLocation.multipleDefinitions": "goto",
	"editor.renderLineHighlight": "none",
	"workbench.startupEditor": "none",
	"workbench.editor.tabSizing": "fixed",
	"workbench.panel.defaultLocation": "right",
	"files.insertFinalNewline": false,

	// "workbench.colorCustomizations": {
	// 	"editorError.foreground": "#00000000"
	// },

	// "workbench.editor.showIcons": false, // Hide Tab File Icons
	// "problems.decorations.enabled": false, // Disable Red Sidebar Errors

	/////////////////////////////////////////////////////////
	// Words wrapping ///////////////////////////////////////
	/////////////////////////////////////////////////////////
	"editor.wordWrap": "bounded",
	"editor.wrappingIndent": "same",
	"editor.wordWrapColumn": 80,

	/////////////////////////////////////////////////////////
	// Fonts & text /////////////////////////////////////////
	/////////////////////////////////////////////////////////
	"editor.fontWeight": "200",
	"editor.fontSize": 17,
	"editor.fontFamily": "JetBrains Mono",
	"editor.bracketPairColorization.enabled": false,
	// "editor.fontFamily": "Fira Code",
	// "editor.fontFamily": "Cascadia Code",
	// "editor.fontFamily": "Geist Mono",

	/////////////////////////////////////////////////////////
	// Terminal & output ////////////////////////////////////
	/////////////////////////////////////////////////////////
	"terminal.integrated.fontSize": 14,
	"[code-runner-output]": { "editor.fontSize": 14 },
	"terminal.integrated.fontFamily": "JetBrains Mono",
	"terminal.integrated.lineHeight": 1.3,

	/////////////////////////////////////////////////////////
	// Cursor & scroll //////////////////////////////////////
	/////////////////////////////////////////////////////////
	"editor.occurrencesHighlight": "off",
	"editor.selectionHighlight": false,
	"editor.minimap.enabled": false,
	// "editor.smoothScrolling": true,
	// "editor.cursorSmoothCaretAnimation": "explicit",
	// "editor.cursorBlinking": "smooth",
	"editor.cursorStyle": "block",

	// Hints
	"editor.quickSuggestionsDelay": 0,

	/////////////////////////////////////////////////////////
	// Explorer & activity bar //////////////////////////////
	/////////////////////////////////////////////////////////
	"explorer.compactFolders": false,
	"explorer.confirmDelete": true,
	"explorer.confirmDragAndDrop": true,
	"explorer.autoOpenDroppedFile": false,
	"explorer.decorations.colors": false,
	"workbench.tree.indent": 24,
	"workbench.tree.renderIndentGuides": "always",
	"workbench.tree.enableStickyScroll": false,
	// "workbench.colorCustomizations": {
	// 	"tree.indentGuidesStroke": "#03fc94",
	// 	"tree.inactiveIndentGuidesStroke": "#6530aa"
	// },

	/////////////////////////////////////////////////////////
	// Format ///////////////////////////////////////////////
	/////////////////////////////////////////////////////////
	"editor.tabSize": 4,
	"editor.formatOnSave": true,
	"editor.codeActionsOnSave": {
		"source.organizeImports": "explicit"
	},
	"prettier.semi": true,
	"prettier.useTabs": true,
	"prettier.singleQuote": true,

	/////////////////////////////////////////////////////////
	// Indent Rainbow Extension Settings ////////////////////
	/////////////////////////////////////////////////////////
	"indentRainbow.indicatorStyle": "light",
	"indentRainbow.lightIndicatorStyleLineWidth": 1,
	"indentRainbow.colors": [
		"rgba(3, 252, 148, 0.5)",
		"rgba(162, 119, 255, 0.5)",
		"rgba(3, 252, 148, 0.5)",
		"rgba(162, 119, 255, 0.5)"
	],
	"files.exclude": {
		"**/.git": false
	},

	/////////////////////////////////////////////////////////
	// Auto-closing tags ////////////////////////////////////
	/////////////////////////////////////////////////////////
	"html.autoClosingTags": true,
	"javascript.autoClosingTags": true,
	"typescript.autoClosingTags": true,
	"auto-close-tag.activationOnLanguage": [
		"xml",
		"php",
		"blade",
		"ejs",
		"jinja",
		"javascript",
		"javascriptreact",
		// "typescript",
		"typescriptreact",
		"plaintext",
		"markdown",
		"vue",
		"liquid",
		"erb",
		"lang-cfml",
		"cfml",
		"HTML (EEx)",
		"HTML (Eex)",
		"plist"
	],

	/////////////////////////////////////////////////////////
	// Live Server Extension Settings ///////////////////////
	/////////////////////////////////////////////////////////
	"liveServer.settings.donotShowInfoMsg": true,
	"liveServer.settings.donotVerifyTags": true,

	/////////////////////////////////////////////////////////
	// Breadcrumbs //////////////////////////////////////////
	/////////////////////////////////////////////////////////
	"breadcrumbs.symbolPath": "off",
	"breadcrumbs.enabled": true,
	"breadcrumbs.icons": false,
	"breadcrumbs.showKeys": false,
	"breadcrumbs.showFiles": false,
	"breadcrumbs.showArrays": false,
	"breadcrumbs.showEvents": false,
	"breadcrumbs.showFields": false,
	"breadcrumbs.showClasses": false,
	"breadcrumbs.showMethods": false,
	"breadcrumbs.showBooleans": false,
	"breadcrumbs.showFunctions": false,
	"breadcrumbs.showConstants": false,
	"breadcrumbs.showEnumMembers": false,
	"breadcrumbs.showConstructors": false,

	/////////////////////////////////////////////////////////
	// JavaScript & TypeScript //////////////////////////////
	/////////////////////////////////////////////////////////
	"javascript.updateImportsOnFileMove.enabled": "always",
	"typescript.updateImportsOnFileMove.enabled": "always",

	/////////////////////////////////////////////////////////
	// Spell Checker ////////////////////////////////////////
	/////////////////////////////////////////////////////////
	"cSpell.language": "en,ru",
	"cSpell.userWords": [],
	"cSpell.enabled": true,
	"editor.unicodeHighlight.allowedCharacters": {
		"а": true,
		"с": true,
		"Т": true,
		"б": true,
		"е": true
	},

	/////////////////////////////////////////////////////////
	// Git Settings /////////////////////////////////////////
	/////////////////////////////////////////////////////////
	"git.openRepositoryInParentFolders": "never",

	/////////////////////////////////////////////////////////
	// Other ////////////////////////////////////////////////
	/////////////////////////////////////////////////////////
	"codeium.enableCodeLens": false,
	"codeium.enableConfig": {
		"*": true
	},
	"gitlens.codeLens.enabled": false,
	"workbench.iconTheme": "quill-icons",
	"workbench.colorTheme": "RCW-120 V1 - @alexeymasasin"
}"}
